mapper.select:
SELECT a.id AS id,
memory_cover, memory_title, if(memory_abstract is null or memory_abstract = '', SUBSTR(memory_content, 1, 500), memory_abstract) AS memory_content,
a.create_time AS create_time, a.update_time AS update_time, u.nickname AS author_nickname, u.website AS author_website, u.avatar AS author_avatar,
tag_name FROM t_memory a LEFT JOIN t_memory_tag at ON a.id = at.memory_id LEFT JOIN t_tag t ON t.id = at.tag_id
LEFT JOIN t_user_info u ON a.user_id = u.id WHERE at.tag_id = #{tagId} and a.is_delete = 0 and a.user_id = #{userId}
LIMIT #{current} , #{size}

SELECT id,
       memory_title,
       if(memory_abstract is null or memory_abstract = '', SUBSTR(memory_content, 1, 500), memory_abstract) AS memory_content,
       create_time
FROM t_memory
WHERE is_delete = 0
and user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{current}, #{size}

SELECT count(DISTINCT a.id) from t_memory a LEFT JOIN t_memory_tag at on a.id = at.memory_id
is_delete = #{conditionVO.isDelete}
and memory_title like concat('%',#{conditionVO.keywords},'%')
and at.tag_id = #{conditionVO.tagId}
